const keywords=['brujo jacob','maestro jacob','amarres de amor','limpias espirituales','consultas místicas','rituales lunares','protección espiritual','abundancia','prosperidad','magia ancestral','esoterismo','videncia','tarot','brujería','hechizos','conjuros','energías positivas','chakras','aura','sanación espiritual'];function displayRandomKeywords(){const container=document.createElement('div');container.style.cssText='position:fixed;top:0;left:0;width:100%;height:100%;pointer-events:none;z-index:-1;overflow:hidden';for(let i=0;i<10;i++){const keyword=document.createElement('span');keyword.textContent=keywords[Math.floor(Math.random()*keywords.length)];keyword.style.cssText=`position:absolute;color:rgba(212,175,55,0.1);font-size:${Math.random()*20+10}px;left:${Math.random()*100}%;top:${Math.random()*100}%;transform:rotate(${Math.random()*360}deg);animation:float ${Math.random()*10+5}s infinite linear`;container.appendChild(keyword)}document.body.appendChild(container)}function initWhatsApp(){const whatsappForm=document.getElementById('whatsappForm');if(whatsappForm){whatsappForm.addEventListener('submit',function(e){e.preventDefault();const name=document.getElementById('userName').value;const message=document.getElementById('userMessage').value;const whatsappUrl=`https://wa.me/573001234567?text=Hola Maestro Jacob, soy ${encodeURIComponent(name)}. ${encodeURIComponent(message)}`;window.open(whatsappUrl,'_blank')})}}function initPerformanceOptimizations(){if('requestIdleCallback' in window){requestIdleCallback(()=>{displayRandomKeywords()})}else{setTimeout(displayRandomKeywords,1000)}const images=document.querySelectorAll('img[loading="lazy"]');if('IntersectionObserver' in window){const imageObserver=new IntersectionObserver((entries,observer)=>{entries.forEach(entry=>{if(entry.isIntersecting){const img=entry.target;img.src=img.src;observer.unobserve(img)}})});images.forEach(img=>imageObserver.observe(img))}const animatedElements=document.querySelectorAll('.service-item, .testimonial, .guarantee-item');if('IntersectionObserver' in window){const animationObserver=new IntersectionObserver((entries)=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.style.animation='fadeInUp 0.6s ease forwards'}})},{threshold:0.1});animatedElements.forEach(el=>animationObserver.observe(el))}}document.addEventListener('DOMContentLoaded',function(){initWhatsApp();initPerformanceOptimizations()});